 74â†’            className="relative w-full aspect-[4/3] sm:aspect-[16/9] md:aspect-[21/9] overflow-hidden rounded-2xl mb-8 cursor-pointer group"
 75â†’            onClick={() => setLightboxIndex(0)}
 76â†’          >
 77â†’            <Image
 78â†’              src={heroPhoto.src}
 79â†’              alt={heroPhoto.alt}
 80â†’              fill
