file.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: #fefaf6;
  --foreground: #2d1a24;
}

@media (prefers-color-scheme: dark) {
  :root {
    --background: #1a0f14;
    --foreground: #f9d0de;
  }
}

html {
  scroll-behavior: smooth;
}

/* Offset for fixed navbar when scrolling to sections */
section[id] {
  scroll-margin-top: 80px;
}

body {
  color: var(--foreground);
  background: var(--background);
  font-family: var(--font-inter), system-ui, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 6px;
}
::-webkit-scrollbar-track {
  background: #fdf2f5;
}
::-webkit-scrollbar-thumb {
  background: #f4a8c0;
  border-radius: 3px;
}
::-webkit-scrollbar-thumb:hover {
  background: #cc2b5e;
}

/* Dark mode scrollbar */
.dark ::-webkit-scrollbar-track {
  background: #1a0f14;
}
.dark ::-webkit-scrollbar-thumb {
  background: #7b1b3e;
}
.dark ::-webkit-scrollbar-thumb:hover {
  background: #ac1d4c;
}

/* iOS parallax fallback */
@supports (-webkit-touch-callout: none) {
  .bg-fixed {
    background-attachment: scroll !important;
  }
}

/* Selection */
::selection {
  background: rgba(244, 168, 192, 0.4);
  color: #7b1b3e;
}

/* Lightbox override */
.yarl__container {
  background: rgba(26, 15, 20, 0.95) !important;
}

/* Scroll offset for fixed nav */
section[id] {
  scroll-margin-top: 80px;
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
  .font-serif {
    font-family: var(--font-cormorant), Georgia, serif;
  }
  .font-script {
    font-family: var(--font-dancing), cursive;
  }
}

