@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
  .font-serif {
    font-family: var(--font-cormorant), Georgia, serif;
  }
  .font-script {
    font-family: var(--font-dancing), cursive;
  }
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }
}

